$("document").ready(function() {    setTimeout(function(){        window.location.reload(1);    }, 70000);    $(document).on('change', '.kv-editable-input', function () {        if($(this).val() == "Rejected"){            $(this).closest('.kv-editable-content').find(".editable_status_dd_div:first").show();        }else{            $(this).closest('.kv-editable-content').find(".editable_status_dd_div:first").hide();            $(this).closest('.kv-editable-content').find(".editable_status_ta_div:first").hide();        }    });        $(document).on('change', '.change_status_dd', function () {        if($(this).val() == "other"){            $(this).closest('.kv-editable-content').find(".editable_status_ta_div:first").show();        }else{            $(this).closest('.kv-editable-content').find(".editable_status_ta_div:first").hide();        }    });});